mapper.insert:
INSERT INTO member (
                     seq
                   , id
                   , pwd
                   , nick
                   , gender
)
VALUES (
#{ seq }
, #{ id }
, #{ pwd }
, #{ nick }
, #{ gender }
)

mapper.select:
SELECT * FROM member WHERE seq = #{ seq }
                       AND nick = #{ nick }
                       AND gender = #{ gender }
